const mongoose = require('mongoose');

const userSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    trim: true
  },
  email: {
    type: String,
    unique: true,
    required: true
  },
  password: {
    type: String,
    required: true
  },
  phone: {
    type: String,
    required: false
  },
  gender: {
    type: String,
    required: false, // Changed to optional
    enum: ['Male', 'Female', null],
    default: null
  },
  dateOfBirth: {
    type: Date,
    required: false, // Changed to optional
    default: null
  },
  shippingAddress: {
    type: String,
    required: false
  },
  isActive: {
    type: Boolean,
    default: true // For status column
  }
}, { timestamps: true });

const User = mongoose.models.User || mongoose.model('User', userSchema);

module.exports = User;